import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ShowCard = ({ show }) => {
  const showDetailPath = `/show/${show.tconst}`;
  const [averageRating, setAverageRating] = useState(null);

  useEffect(() => {
    // Make an API request to fetch the averageRating for the current show.tconst
    fetch(`http://127.0.0.1:8000/shows/api/titleratings/${show.tconst}/`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setAverageRating(data.averageRating); // Assuming the API returns an array with one item
      })
      .catch((error) => {
        console.error('Error fetching averageRating:', error);
      });
  }, [show.tconst]);

  return (
    <Link to={showDetailPath} className="show-card">
      <div>
        <h4>{show.primaryTitle}</h4>
        <p>Start Year: {show.startYear}</p>
        <p>End Year: {show.endYear}</p>
        <p>Type: {show.titleType}</p>
        <p>Is Adult: {show.isAdult ? 'Yes' : 'No'}</p>
        <p>Genres: {show.genres.map((genre) => genre.name).join(', ')}</p>
        {averageRating !== null && (
          <p>Average Rating: {averageRating}</p>
        )}
      </div>
    </Link>
  );
};

export default ShowCard;
