import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import NavbarMovie from './NavbarMovie';

const ActorDetails = () => {
  const { nconst } = useParams();
  const [name, setName] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Define the API URL based on showId or any other criteria
    const apiUrl = `http://127.0.0.1:8000/shows/api/detail_cast/?ID=${nconst}`;

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setIsLoading(false);
        setName(data);
      })
      .catch((error) => {
        setIsLoading(false);
        setError(error);
      });
  }, [nconst]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="show-details">
      <NavbarMovie />
      <h2>Show Details</h2>
      {show && (
        <div>
          <h4>{show.primaryTitle}</h4>
          <p>Start Year: {show.startYear}</p>
          <p>End Year: {show.endYear}</p>
          <p>Type: {show.titleType}</p>
          <p>Is Adult: {show.isAdult ? 'Yes' : 'No'}</p>
          <p>Genres: {show.genres.map((genre) => genre.name).join(', ')}</p>
        </div>
      )}
    </div>
  );
};

export default ShowDetails;
