// SearchBar.js
import React, { useState } from 'react';

const SearchBar = ({ onSearch, filterOption, setFilterOption }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // Function to handle search term change
  const handleSearchChange = (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);
  };

  // Function to handle filter option change
  const handleFilterOptionChange = (event) => {
    const newFilterOption = event.target.value;
    setFilterOption(newFilterOption); // Update the filterOption state
  };

  // Function to handle search button click
  const handleSearchClick = () => {
    onSearch(searchTerm); // Call the parent component's onSearch function with the search term
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search shows"
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <select
        value={filterOption}
        onChange={handleFilterOptionChange}
      >
        <option value="">Select an option</option>
        <option value="titleType">Title Type</option>
        <option value="isAdult">Is Adult</option>
        <option value="genresName">Genres Name</option>
      </select>
      <button onClick={handleSearchClick}>Search</button>
    </div>
  );
};

export default SearchBar;
