const SearchBar = ({ onSearch, onFilter, onSort }) => {
  const handleSearch = (e) => {
    onSearch(e.target.value);
  };

  const handleFilter = () => {
    onFilter({ titleType, isAdult });
  };

  const handleSort = () => {
    onSort(orderingField);
  };

  return (
    <div>
      <input type="text" placeholder="Search..." onChange={handleSearch} />
      <select value={titleType} onChange={(e) => setTitleType(e.target.value)}>
        <option value="">Select Title Type</option>
        <option value="movie">Movie</option>
        <option value="tvSeries">TV Series</option>
        {/* Add more options as needed */}
      </select>
      <label>
        Adult Content:
        <input
          type="checkbox"
          checked={isAdult}
          onChange={(e) => setIsAdult(e.target.checked)}
        />
      </label>
      <select value={orderingField} onChange={(e) => setOrderingField(e.target.value)}>
        <option value="">Sort by...</option>
        <option value="fieldName1">Field Name 1</option>
        <option value="fieldName2">Field Name 2</option>
        {/* Add more sorting options based on your API */}
      </select>
      <button onClick={handleFilter}>Filter</button>
      <button onClick={handleSort}>Sort</button>
    </div>
  );
};
