import React, { useState } from 'react';

const SearchBar = ({ onSearch, filterField, setFilterField }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);
    onSearch(newSearchTerm, filterField); // Pass both searchTerm and filterField to the parent component
  };

  const handleFilterFieldChange = (event) => {
    const newFilterField = event.target.value;
    setFilterField(newFilterField);
    onSearch(searchTerm, newFilterField); // Pass both searchTerm and newFilterField to the parent component
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search shows"
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <select value={filterField} onChange={handleFilterFieldChange}>
        <option value="titleType">Title Type</option>
        <option value="isAdult">Is Adult</option>
        <option value="genresName">Genres Name</option>
      </select>
    </div>
  );
};

export default SearchBar;
