import React, { useEffect, useState } from 'react';
import NavbarMovie from '../LIst/NavbarMovie';
import { Link } from 'react-router-dom';

function UserProfile() {
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch user profile data from the Django API using JWT token for authentication
    const token = localStorage.getItem('jwtToken'); // Assuming you store the token in localStorage

    if (token) {
      fetch('http://127.0.0.1:8000/api/get_user_profile/', {
        headers: {
          Authorization: `Bearer ${token}`, // Attach the JWT token to the request
        },
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Unauthorized'); // Handle unauthorized access
          }
          return response.json();
        })
        .then((data) => {
          setUser(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching user profile:', error);
          setLoading(false);
        });
    } else {
      // Handle case where no token is available (user not authenticated)
      setLoading(false);
    }
  }, []);

  return (
    <div>
      <NavbarMovie />
      <Link to="/editUser">
        <button>Edit Profile</button>
      </Link>
      <h1>User Profile</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <p>Email: {user.email}</p>
          <p>Country: {user.country}</p>
          <p>Age: {user.age}</p>
        </>
      )}
    </div>
  );
}

export default UserProfile;
