import React, { useState } from 'react';

const ReviewPrompt = ({ onReviewSubmit }) => {
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const handleReviewSubmit = () => {
    if (rating && review) {
      // Call a function to submit the review data (e.g., to a backend)
      onReviewSubmit({ rating, review });
      // Reset the form
      setRating('');
      setReview('');
      setIsOpen(false);
    } else {
      alert('Please provide a valid rating and review.');
    }
  };

  return (
    <div className="review-prompt">
      <button onClick={() => setIsOpen(true)}>Review Show</button>
      {isOpen && (
        <div className="review-box">
          <h2>Rate the Show (1-10)</h2>
          <input
            type="number"
            min="1"
            max="10"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            placeholder="Rating"
          />
          <textarea
            value={review}
            onChange={(e) => setReview(e.target.value)}
            placeholder="Write your review..."
          />
          <button onClick={handleReviewSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
};

export default ReviewPrompt;
