import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import ShowCard from './ShowCard';

const ShowList = () => {
  const [shows, setShows] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredShows, setFilteredShows] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterPattern, setFilterPattern] = useState('');
  const [sortField, setSortField] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [originalOrder, setOriginalOrder] = useState([]); // Store the original order

  const fetchData = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://127.0.0.1:8000/shows/api/titles/?page=${page}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setShows(data.results);
      setTotalPages(data.totalPages);
      setOriginalOrder([...data.results]); // Save the original order
    } catch (error) {
      console.error('Error fetching data:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [page]);

  useEffect(() => {
    // Filter the shows based on the search term and filter pattern
    const filtered = shows.filter((show) =>
      show.primaryTitle.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (filterPattern === '' || 
       show.startYear.toString().includes(filterPattern) ||
       show.endYear.toString().includes(filterPattern) ||
       show.titleType.toLowerCase().includes(filterPattern.toLowerCase()) ||
       show.genres.some(genre => genre.name.toLowerCase().includes(filterPattern.toLowerCase())))
    );
    setFilteredShows(filtered);
  }, [searchTerm, shows, filterPattern]);

  useEffect(() => {
    // Check if the search query is empty
    if (searchQuery === '') {
      setSearchTerm('');
      setFilterPattern('');
    }
  }, [searchQuery]);

  const handleSearchClick = () => {
    setSearchTerm(searchQuery);
  };

  const handleFilterClick = () => {
    setFilterPattern(searchQuery);
  };

  const handleSortClick = () => {
    // Toggle the sortOrder between 'asc' and 'desc'
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    // Sort the shows based on the selected field and order
    const sorted = [...filteredShows];
    sorted.sort((a, b) => {
      if (sortOrder === 'asc') {
        return a[sortField].localeCompare(b[sortField]);
      } else {
        return b[sortField].localeCompare(a[sortField]);
      }
    });
    setFilteredShows(sorted);
  };

  const handleRevertSortClick = () => {
    // Revert sorting to the original order
    setFilteredShows([...originalOrder]);
    setSortField('');
    setSortOrder('asc');
  };

  const handlePageChange = (newPage) => {
    setPage(newPage);
    window.location.reload(); // Reload the site
  };

  const handleIncrementPage = () => {
    if (page < totalPages) {
      handlePageChange(page + 1);
    }
  };

  const handleDecrementPage = () => {
    if (page > 1) {
      handlePageChange(page - 1);
    }
  };


  return (
    <div>
      <h2>List of Shows</h2>
      <SearchBar
        onSearch={(value) => setSearchQuery(value)}
      />
      <button onClick={handleSearchClick}>Search</button>
      <button onClick={handleFilterClick}>Filter</button>
      <label>
        Sort by:
        <select value={sortField} onChange={(e) => setSortField(e.target.value)}>
          <option value="primaryTitle">Title</option>
          <option value="startYear">Start Year</option>
          <option value="endYear">End Year</option>
          <option value="titleType">Type</option>
        </select>
      </label>
      <button onClick={handleSortClick}>Toggle Sort Order</button>
      <button onClick={handleRevertSortClick}>Revert Sorting</button>
      {isLoading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <>
          <div>
            <h3>Results:</h3>
            <div className="show-card-container">
              {filteredShows.map((show) => (
                <ShowCard key={show.tconst} show={show} />
              ))}
            </div>
          </div>
          <div>
            <ul className="pagination">
              <li>
                <button onClick={handleDecrementPage}>Previous</button>
              </li>
              {Array.from({ length: totalPages }, (_, index) => (
                <li key={index} className={index + 1 === page ? 'active' : ''}>
                  <button onClick={() => handlePageChange(index + 1)}>{index + 1}</button>
                </li>
              ))}
              <li>
                <button onClick={handleIncrementPage}>Next</button>
              </li>
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

export default ShowList;
